INSERT INTO `tags` VALUES ('1', 'nuoc-la', 'nước lã');
INSERT INTO `tags` VALUES ('2', 'nu-ngoai-cam', 'Nữ ngoại cảm');
INSERT INTO `tags` VALUES ('3', 'trum-giang-ho', 'trùm giang hồ');
